import { useState } from 'react'
import './App.css'

import Cards from './components/Cards/Cards.jsx'
import Nav from './components/Nav/Nav.jsx'
import About from './views/About.jsx'
import Detail from './views/Detail.jsx'

import { Route, Routes } from 'react-router-dom'


function App () {
  const [characters,setCharacters] = useState([]);

  const onSearch = (id) => {
    const URL = 'https://be-a-rym.up.railway.app/api';
    const KEY = 'ab609bfc7704.d3a36031c2ddc6820402';

    if (characters.find((char) => char.id === id)) {
      alert('Personaje Repetido')
    }

    fetch(`${URL}/character/${id}?key=${KEY}`)
      .then((response) => response.json())
      .then((data) => {
      if (data.name ) {
        setCharacters((oldChar) => [...oldChar, data]); //Recibo el estado viejo y retorno el estado nuevo.
      } else {
        alert('No hay personajes con ese ID');
      }
    });  
  }

  const onClose = (id) => {
    setCharacters(characters.filter((char) => char.id !== id));
  }

  return (
    <div className='App' style={{ padding: '25px' }}>
      <Nav onSearch={onSearch}/>
      
      <Routes>
        <Route path='/home' element={<Cards characters={characters} onClose={onClose}/>}/>

        <Route path='/about' element={<About PONER PROPS/>}/>

        <Route path='/detail/:detailId' element={<Detail PONERPROPS/>}/>
      </Routes>
      
    </div>
  )
} 

/*ADENTRO DEL RETURN y adentro de DIV
  <div>
        <Nav onSearch={onSearch}/>
      </div>
      <Cards characters={characters} onClose={onClose}/>         
*/

export default App;
