import { ADD_CHAR,DEL_CHAR,FILTER,ORDER } from "./actions"

const initialState = {
    myFavorites: [],
    allCharacters: []
}

const rootReducer = (state = initialState,action) =>{
    switch(action.type){
        case ADD_CHAR: return {
            ...state,
            myFavorites: [...state.allCharacters, action.payload ],
            allCharacters: [...state.allCharacters, action.payload ]
        }

        case DEL_CHAR: return {
            ...state,
            myFavorites: state.myFavorites.filter((char) => char.id !== action.payload )    //DUDA
        };  

        case FILTER: return {
            ...state,
            myFavorites: state.allCharacters.filter((char)=> char.gender === action.payload)        //DUDA
        };

        case ORDER: 
            if(state.payload === 'Ascendente') return {...state, myFavorites: state.myFavorites.sort((a,b) => a-b)}
            if(state.payload === 'Descendente') return {...state, myFavorites: state.myFavorites.sort((a,b) => a-b).reverse()}
        break;
        
        default: return {...state}
    }
}

export default rootReducer;
