import styled from "styled-components"

export const ContainerGlobal = styled.div`
    background-color: white;
    border-radius: 0px 0px 20px 20px;
    height: 570px;

    display: flex;
    justify-content: space-around;
`

export const InfoRyM = styled.div`
    background-color: brown;
    border-radius: 20px;
    height: 570px;
    width: 70%;
    

`
export const InfoAboutMe = styled.div`
    background: url("https://wallpaperaccess.com/full/2314983.jpg");
    height: 540px;
    width: 25%;
    margin-top: 1%;

    box-shadow: 0px 3px 11px 7px rgba(0,0,0,0.75);
    border-radius: 20px; 

`
export const ImgProfile = styled.img`
    width: 30%;
    border-radius: 20px;    
    box-shadow: 0px 3px 11px 7px rgba(0,0,0,0.75);

`

export const ContImgs = styled.div`
    width: 100%;
`

export const LinksImgs = styled.img`
    width: 10%;
    border-radius: 20px;
`
