import { ADD_CHAR,REMOVE_FAVORITE,FILTER_FAVORITES,ORDER_FAVORITES, GET_CHARACTER_DETAIL, CLEAN_DETAIL, GET_FAVORITES } from "./actions"

const initialState = {
    myFavorites: [],
    //allCharacters: [],
    characterDetail: {}
}

const rootReducer = (state = initialState,action) =>{
    switch(action.type){
        case ADD_CHAR: return {
            ...state,
            myFavorites: [...state.myFavorites, action.payload ],
            //allCharacters: [...state.allCharacters, action.payload ]
        }

        case REMOVE_FAVORITE: return {
            ...state,
            myFavorites: state.myFavorites.filter((char) => char.id !== action.payload )    //allChar
        };  

        case FILTER_FAVORITES: return {
            ...state,
            myFavorites: state.myFavorites.filter((char)=> char.gender === action.payload)  //allChar      
        };

        case ORDER_FAVORITES: 
        
        const allFavs = state.myFavorites; //allChar

        if(action.payload === 'Descendente'){
            let descFavs = allFavs.sort((fav1, fav2) => {
                (fav1.name < fav2.name) ? 1 : (fav1.name > fav2.name) ? -1 : 0;
            })
            return {...state, myFavorites : descFavs};
        }
        if(action.payload === 'Ascendente'){
            let ascFavs = allFavs.sort((fav1, fav2) => {
                (fav1.name > fav2.name) ? 1 : (fav1.name < fav2.name) ? -1 : 0;
            })
            return {...state, myFavorites : ascFavs};
        }

        return {...state};

        case GET_FAVORITES: return {
            ...state,
            myFavorites: action.payload
        }
        
        case GET_CHARACTER_DETAIL : return { 
            ...state, 
            characterDetail: action.payload
        }

        case CLEAN_DETAIL: return {
            ...state,
            characterDetail: {}
        }

        default: return {...state}
    }
}

export default rootReducer;
