import styled from "styled-components";


export const ContainerGlobal = styled.div`
    height: 550px;

    padding-top: 10px;
    padding-bottom: 20px;

    display: flex;
    flex-direction: row;
    justify-content: space-around;
    
`

//----------------------------------------------------
export const Inicio = styled.div`
    background: url("https://img.freepik.com/premium-photo/fantasy-galaxy-portal-buildings-future-doors-unreal-3d-rendering-raster-illustration_717906-330.jpg");
    width: 60%;
    height: 400px;
    border-radius: 20px;
    box-shadow: -8px 9px 14px -3px rgba(0,0,0,0.75);
    
    margin-top: 75px;

    display: flex;
    flex-direction: column;
    justify-content: center;

    
` 
//----------------------------------------------------
export const Titulos = styled.div`
    font-family: sans-serif;
    font-style: oblique;
    color: #7d796c;
    text-shadow: 1px 1px white;
    text-decoration: underline;
` 

export const ImgRyM = styled.img`
    width: 500px;
    border-radius: 20px;
`


//-----------------------------------------------------
export const Info = styled.div`
    background-color: white;
    height: 400px;
    width: 300px;
    border-radius: 20px;
    box-shadow: -8px 9px 14px -3px rgba(0,0,0,0.75);

    padding-top: 20px;
    padding-left: 20px;
    padding-right: 20px;
    margin-top 75px;

    display: flex;
    flex-direction: column;
    justify-content: space-around;
    
` 

export const TitleInfo = styled.div`
    border-radius: 20px;
    background-color: black;
    color: white;
    width: 100%;
`

export const Publi = styled.div`
    height: 70px;
    display:flex;
    flex-direction: row;
    justify-content: space-around;
    
` 

export const ImgHenry = styled.img`
    width: 40px;
    height: 40px;
    box-shadow: -8px 9px 14px -3px rgba(0,0,0,0.75);
    border-radius: 20px;
`
//-----------------------------------------------------
