import styled from "styled-components"

const Carta = styled.div`
   border-style: solid;
   order-width: 3px;
   border-radius: 20px;
   
   background-image: url('https://images.pexels.com/photos/235985/pexels-photo-235985.jpeg?cs=srgb&dl=pexels-pixabay-235985.jpg&fm=jpg');

   padding-top: 2%;
   margin-bottom: 2%;
   width: 15%;

`
const Boton = styled.button`
   border-style: solid;
   border-radius: 50%;
   color: white;
   background-color: #9C1E03;

`

const Imagen = styled.img`
   width: 50%;
   border-style: solid;
   border-radius: 50%;
`
const Datos = styled.h2`
   font-size: small;
`

export default function Card({name,species,gender,image}) {
   const OnClose = () => window.alert('Emulamos que se cierra la card')
   return (
      <Carta>
         <Boton onClick={OnClose}>X</Boton>
         <Imagen  src={image} alt="" />
         <Datos>Nombre: {name}</Datos>
         <Datos>Especie: {species}</Datos>
         <Datos>GÃ©nero: {gender}</Datos>
         
      </Carta>
   );
}
