import React from "react";
import validation from "./validation.js";
import {ContainerGlobal,Inicio,Titulos,Info} from "./Form.js";


const Form = ({login}) => {
    const [userData, setUserData] = React.useState({
        username: '',
        password: '' 
    });
    
    const [errors, setErrors] = React.useState({
        username: '', 
        password: '' 
    });

    const handleInputChange = (event) =>{
        const prop = event.target.name;
        const value = event.target.value;

        setUserData({...userData,[prop]: value,});
        validation({...userData,[prop]: value,},setErrors,errors);  //Valido los datos ingresados y le envío errors y setErrors.
    };

    const handleSubmit = (event) =>{
        event.preventDefault();
        login(userData);
    }

    return (
        <ContainerGlobal>
            <Inicio>
                <Titulos>
                    <h1>Bienvenido a Rick and Morty API</h1>
                    <h3>Hecho por Gonzalo Nicolás Púa</h3>
                </Titulos> 

                <form onSubmit={handleSubmit}> 
                    <div>
                        <label htmlFor="username">Username:</label>
                        <input type='text' name="username" value={userData.username} onChange={handleInputChange}/>
                    </div>            
                    <div>
                        <label htmlFor="password">Password:</label>
                        <input type='text' name="password" value={userData.password} onChange={handleInputChange}/>
                    </div>
                    <button>Login</button>
                </form>    
            </Inicio>
            <Info>
                <h3>Full Stack Developer Career</h3>
                <ul>
                    <li>Módulo 1: Approved</li>
                    <li>Módulo 2: Approved</li>
                    <li>Módulo 3: Approved</li>
                    <li>Módulo 4: Pending...</li>
                </ul>
                <p>Patrocinado por <a href="https://www.soyhenry.com/">soyHenry</a></p>
            </Info>
        </ContainerGlobal>
    )
}

export default Form;
