import styled from "styled-components";


export const ContainerGlobal = styled.div`
    height: 550px;
    display: flex;
    flex-direction: row;
    justify-content: space-around;

    
`
export const Inicio = styled.div`
    background-color: #994C00;
    width: 60%;
    height: 400px;
    border-radius: 20px;
    box-shadow: -8px 9px 14px -3px rgba(0,0,0,0.75);
    
    margin-top: 75px;

    display: flex;
    flex-direction: column;
    justify-content: center;

    
` 

export const Titulos = styled.div`
` 

export const Info = styled.div`
    background-color: white;
    height: 400px;
    width: 300px;
    border-radius: 20px;
    box-shadow: -8px 9px 14px -3px rgba(0,0,0,0.75);

    padding-top: 20px;
    padding-left: 10px;
    margin-top 75px;

    display: flex;
    flex-direction: column;
    
` 

export const Publi = styled.div`
    display:flex;
    flex-direction: row;
` 

export const ImgHenry = styled.img`
    width: 20px ;
    border-radius: 20px;
`
